import React, { Component } from 'react'; //React hooks are using 'useSomething'
import './App.css';
import Person from './Person/Person';


class App extends Component {

  useState = () => ({ // Returns 2 elements (currentState, functionToUpdateState)
    persons: [
      { name: 'Max', age: 28 },
      { name: 'Trist', age: 29},
      { name: 'Louis', age: 26 }
    ]
  });

  // Function
  switchNameHandler = () => {
    // On button click, change the properties of the given state
    this.setState({
      persons: [
        { name: 'Maxxxxx', age: 28 },
        { name: 'Tristtttttt', age: 29},
        { name: 'Louissssss', age: 26 }
      ]
    });
  }

  render(){
    return (
      // JSX Code Below, not HTML
      <div className="App">
        <h1>Hi, I am a React app</h1>  
        <h3>Another heading</h3>
        <button onClick = {switchNameHandler}>Switch Name</button>
        <Person name = {personsState.persons[0].name} 
                age = {personsState.persons[0].age}>
        </Person>
        <Person name = {personsState.persons[1].name} 
                age = {personsState.persons[1].age}>My Hobbies: Racing
        </Person>
        <Person name = {personsState.persons[2].name} 
                age = {personsState.persons[2].age}>
        </Person>
      </div>
    );
  }

}

export default App;
