import React, { Component } from 'react'; //React hooks are using 'useSomething'
import './App.css';
import Person from './Person/Person';


class App extends Component {

  state = { // Returns 2 elements (currentState, functionToUpdateState)
    persons: [
      { name: 'Max', age: 28 },
      { name: 'Trist', age: 29},
      { name: 'Louis', age: 26 }
    ],
    otherState: 'Some other value'
  };

  // Function
  switchNameHandler = (newName) => {
    // On button click, change the properties of the given state
    this.setState({
      persons: [
        { name: newName, age: 28 },
        { name: 'Tristtttttt', age: 29},
        { name: 'Louissssss', age: 26 }
      ]
    });
  }

  render(){
    return (
      // JSX Code Below, not HTML
      <div className="App">
        <h1>Hi, I am a React app</h1>  
        <h3>Another heading</h3>
        <button onClick = {() => this.switchNameHandler.bind("TRISTENAGUILAR!")}>Switch Name</button>
        <Person name = {this.state.persons[0].name} 
                age = {this.state.persons[0].age}>
        </Person>
        <Person name = {this.state.persons[1].name} 
                age = {this.state.persons[1].age} 
                click={this.switchNameHandler.bind(this, 'TristenAguilar!')}>My Hobbies: Racing
        </Person>
        <Person name = {this.state.persons[2].name} 
                age = {this.state.persons[2].age}>
        </Person>
      </div>
    );
  }

}

export default App;
