import React, { Component } from 'react'; //React hooks are using 'useSomething'
import './App.css';
import Person from './Person/Person';


class App extends Component {

  state = { // Returns 2 elements (currentState, functionToUpdateState)
    persons: [
      { name: 'Max', age: 28 },
      { name: 'Trist', age: 29},
      { name: 'Louis', age: 26 }
    ],
    otherState: 'Some other value'
  };

  // Function
  switchNameHandler = (newName) => {
    // On button click, change the properties of the given state
    this.setState({
      persons: [
        { name: newName, age: 28 },
        { name: 'Tristtttttt', age: 29},
        { name: 'Louissssss', age: 26 }
      ]
    });
  }

  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        { name: 'Gaby', age: 28 },
        { name: event.target.value, age: 29 },
        { name: 'Louissssss', age: 26 }
      ]
    })
  }

  render(){

    // Inline Styling
    const buttonStyle = {
      backgroundColor: 'white',
      font: 'inherit',
      border:'1x solid blue',
      padding: '8px',
      cursor: 'pointer'
    };

    return (
      // JSX Code Below, not HTML
      <div className="App">
        <h1>Hi, I am a React app</h1>  
        <h3>Another heading</h3>
        <button 
          style = {buttonStyle}
          onClick = {this.switchNameHandler('TristenAguilar')}>Switch Name</button>
        <div>

        </div>
        <Person name = {this.state.persons[0].name} 
                age = {this.state.persons[0].age}>
        </Person>
        <Person name = {this.state.persons[1].name} 
                age = {this.state.persons[1].age} 
                click={this.switchNameHandler.bind(this, 'TristenAguilar!')}
                changed={this.nameChangeHandler} > My Hobbies: Racing
        </Person>
        <Person name = {this.state.persons[2].name} 
                age = {this.state.persons[2].age}>
        </Person>
      </div>
    );
  }

}

export default App;
